mapper.update:
		UPDATE SUBSCRIPTION 
  		   SET REMAIN_CLASSES = (REMAIN_CLASSES - 1)
        WHERE NO = #{no}

mapper.insert:
  		INSERT INTO SUBSCRIPTION (NO, PAYMENT_NO, MEMBER_ID , PASS_NO, START_DATE, END_DATE, REMAIN_CLASSES)
		VALUES (
			SEQ_SUBSCRIPTION_NO.nextval
		  , #{paymentNo}
		  , #{memberId}
		  , #{passNo}
		  , SYSDATE
		  , TO_DATE(#{endDate}, 'yyyy-mm-dd hh24:mi:ss')
		  , #{remainClasses}		  
		)
